mapper.select:
SELECT COUNT(*) FROM BOARD B JOIN MEMBER ON (BOARD_WRITER = USER_NO)
WHERE B.BOARD_TITLE LIKE '%'||#{keyword}||'%' OR USER_NAME LIKE '%'||#{keyword}||'%'
AND DEPT_CODE = #{deptCode}
WHERE (B.BOARD_TITLE LIKE '%'||#{keyword}||'%' OR USER_NAME LIKE '%'||#{keyword}||'%') AND DEPT_CODE = #{deptCode}
AND B.STATUS ='Y'

SELECT B.BOARD_NO, B.BOARD_TITLE, USER_NAME, DEPT_NAME, B.CATEGORY_CODE, B.IMPORTANCE_LEVEL, B.COUNT, B.CREATE_DATE FROM BOARD B JOIN MEMBER ON (B.BOARD_WRITER = USER_NO) JOIN DEPARTMENT USING (DEPT_CODE)
WHERE B.BOARD_TITLE LIKE '%'||#{keyword}||'%' OR USER_NAME LIKE '%'||#{keyword}||'%'
AND DEPT_CODE = #{deptCode}
WHERE (B.BOARD_TITLE LIKE '%'||#{keyword}||'%' OR USER_NAME LIKE '%'||#{keyword}||'%') AND DEPT_CODE = #{deptCode}
AND B.STATUS ='Y' ORDER BY B.BOARD_NO DESC

mapper.update:
UPDATE BOARD 
	SET COUNT = COUNT+1 
	WHERE BOARD_NO = #{bno}

UPDATE BOARD_ATTACHMENT
	SET STATUS = 'N'
	WHERE REF_BNO = #{boardNo}

UPDATE BOARD
	SET STATUS = 'N'
	WHERE BOARD_NO = #{boardNo}
