mapper.select:
SELECT COUNT(*)
FROM RECOMMEND
WHERE REF_BNO = #{boardNo}
AND REF_UNO = #{userNo}

SELECT COUNT(*)
FROM RECOMMEND
WHERE REF_BNO = #{boardNo}

SELECT COUNT(*)
FROM DEPRECATED
WHERE REF_BNO = #{boardNo}
AND REF_UNO = #{userNo}

SELECT COUNT(*)
FROM DEPRECATED
WHERE REF_BNO = #{boardNo}

 		SELECT REPLY_NO, REF_BNO, USER_NAME AS REPLY_WRITER, REPLY_CONTENT, CREATE_DATE
 		FROM REPLY R
        JOIN MEMBER M ON(R.REPLY_WRITER = M.USER_NO)
 		WHERE R.STATUS = 'Y'
 		
 			
		 		AND REF_BNO = #{boardNo} 			
 			
 			
 				AND REPLY_WRITER = #{userNo}

 		SELECT COUNT(*)
 		FROM REPLY
 		
 			
		 		WHERE REF_BNO = #{boardNo}
 			
 			
 				WHERE REPLY_WRITER = #{userNo}

mapper.update:
UPDATE BOARD
SET CATEGORY_CODE = #{categoryCode}, BOARD_TITLE = #{boardTitle}, BOARD_CONTENT = #{boardContent}, MODIFY_DATE = SYSDATE
WHERE BOARD_NO = #{boardNo}

UPDATE BOARD
SET STATUS = 'N'
WHERE BOARD_NO = #{boardNo}

UPDATE REPLY
SET REPLY_CONTENT = #{replyContent}, MODIFY_DATE = SYSDATE
WHERE REPLY_NO = #{replyNo}

UPDATE REPLY
SET STATUS = 'N'
WHERE REPLY_NO = #{replyNo}
