mapper.select:
SELECT COUNT(*) FROM MEMBER WHERE STATUS = 'Y'
AND USER_NAME LIKE '%'||#{keyword}||'%'
AND DEPT_CODE = #{deptCode}
AND USER_NAME LIKE '%'||#{keyword}||'%' AND DEPT_CODE = #{deptCode}

SELECT USER_NO, USER_NAME, USER_ID, EMAIL, TO_CHAR(BIRTH, 'YYYY-MM-DD')AS BIRTH, JOB_NAME, DEPT_NAME, TO_CHAR(EMPOLYMENT_DATE,'YYYY-MM-DD') AS EMPOLYMENT_DATE FROM MEMBER JOIN DEPARTMENT USING(DEPT_CODE) JOIN JOB USING(JOB_CODE) WHERE STATUS = 'Y'
AND USER_NAME LIKE '%'||#{keyword}||'%'
AND DEPT_CODE = #{deptCode}
AND USER_NAME LIKE '%'||#{keyword}||'%' AND DEPT_CODE = #{deptCode}
ORDER BY JOB_CODE
