mapper.select:
SELECT * FROM (SELECT ROWNUM, A.* FROM (SELECT DOC_NO ,DEPT_NAME ,DOC_NAME ,DOC_TITLE ,DOC_WRITER ,SECOND_APPROVER_NO ,LAST_APPROVER_NO ,EMERGENCY ,STATUS ,TO_CHAR(SECOND_DATE, 'YYYY-MM-DD') "SECOND_DATE" ,TO_CHAR(LAST_DATE, 'YYYY-MM-DD') "LAST_DATE" ,TO_CHAR(CREATE_DATE,'YYYY-MM-DD')"CREATE_DATE" FROM APPROVAL_DOC P JOIN DEPARTMENT USING (DEPT_CODE) JOIN DOC_TYPE USING (DOC_TYPE) JOIN APPROVAL USING (DOC_NO)
WHERE P.STATUS = #{status}
ORDER BY DECODE(EMERGENCY,'Y',2), P.CREATE_DATE DESC ) A) WHERE ROWNUM BETWEEN 1 AND 5

 		SELECT COUNT(*)
 		FROM DOC_TYPE


	 			WHERE DOC_NAME LIKE '%'||#{keyword}||'%'


 				WHERE DOC_CATEGORY = #{docCategory}


	 			WHERE DOC_NAME LIKE '%'||#{keyword}||'%'
	 			AND DOC_CATEGORY = #{docCategory}

 		SELECT * 
 		FROM DOC_TYPE


	 			WHERE DOC_NAME LIKE '%'||#{keyword}||'%'


 				WHERE DOC_CATEGORY = #{docCategory}


	 			WHERE DOC_NAME LIKE '%'||#{keyword}||'%'
	 			AND DOC_CATEGORY = #{docCategory}

mapper.update:
UPDATE APPROVAL
SET RETURN_REASON = #{returnReason}
   ,SECOND_SIGNATURE = '반려'
   ,SECOND_DATE = SYSDATE
WHERE DOC_NO = #{docNo}

		UPDATE APPROVAL
 		SET RETURN_REASON = #{returnReason}
 		   ,LAST_SIGNATURE = '반려'
 		   ,LAST_DATE = SYSDATE
 		WHERE DOC_NO = #{docNo}

UPDATE APPROVAL_DOC
SET STATUS = #{status}
   ,EMERGENCY = 'N'
WHERE DOC_NO = #{docNo}

UPDATE APPROVAL
SET SECOND_SIGNATURE = #{secondSignature}
   ,SECOND_DATE = SYSDATE
WHERE DOC_NO = #{docNo}

		UPDATE APPROVAL
 		SET LAST_SIGNATURE = #{lastSignature}
 		   ,LAST_DATE = SYSDATE
 		WHERE DOC_NO = #{docNo}
