mapper.select:
SELECT COUNT(*) FROM BOARD B JOIN MEMBER M ON(B.BOARD_WRITER = M.USER_NO) WHERE B.STATUS = 'Y' AND CATEGORY_CODE = 'B1'
AND (B.BOARD_TITLE LIKE '%'||#{keyword}||'%' OR BOARD_CONTENT LIKE '%'||#{keyword}||'%')
AND DEPT_CODE = #{status}
AND (B.BOARD_TITLE LIKE '%'||#{keyword}||'%' OR BOARD_CONTENT LIKE '%'||#{keyword}||'%') AND DEPT_CODE = #{status}

SELECT R.REF_UNO, B.BOARD_NO, M.USER_NAME, D.DEPT_NAME, B.BOARD_TITLE, B.BOARD_CONTENT, TO_CHAR(B.CREATE_DATE,'YYYY-MM-DD') "CREATE_DATE", B.COUNT, B.IMPORTANCE_LEVEL, A.ATTACHMENT_NO FROM (SELECT * FROM (SELECT * FROM BOARD WHERE CATEGORY_CODE = 'B1' AND IMPORTANCE_LEVEL = 'I' AND STATUS = 'Y' ORDER BY CREATE_DATE DESC)
UNION ALL
SELECT * FROM (SELECT * FROM BOARD WHERE CATEGORY_CODE = 'B1' AND IMPORTANCE_LEVEL = 'R' AND STATUS = 'Y' ORDER BY CREATE_DATE DESC))B JOIN MEMBER M ON( B.BOARD_WRITER = M.USER_NO) LEFT JOIN DEPARTMENT D USING(DEPT_CODE) LEFT JOIN BOARD_ATTACHMENT A ON(B.BOARD_NO = A.REF_BNO) LEFT JOIN RECOMMEND R ON(B.BOARD_NO = R.REF_BNO) WHERE B.STATUS = 'Y'
AND (B.BOARD_TITLE LIKE '%'||#{keyword}||'%' OR BOARD_CONTENT LIKE '%'||#{keyword}||'%')
AND DEPT_CODE = #{status}
AND (B.BOARD_TITLE LIKE '%'||#{keyword}||'%' OR BOARD_CONTENT LIKE '%'||#{keyword}||'%') AND DEPT_CODE = #{status}

mapper.update:
UPDATE REPLY
SET STATUS = 'N'
WHERE REPLY_NO = #{replyNo}

mapper.select:
  	SELECT COUNT(*)
	FROM RECOMMEND
	WHERE REF_BNO = #{boardNo}
	AND REF_UNO = #{userNo}
	AND TYPE = 'B'

mapper.delete:
  	DELETE RECOMMEND
  	WHERE REF_BNO = #{boardNo}
	AND REF_UNO = #{userNo}
	AND TYPE = 'B'
